export function useMutationObserver<T>(
  targetNode: Node,
  config: MutationObserverInit,
  callback: (mutationList: [MutationRecord], observer: MutationObserver) => T
): T;


export function useMutationObserverOnce<T>(
  targetNode: Node,
  config: MutationObserverInit,
  callback: (mutationList: [MutationRecord], observer: MutationObserver) => T
): T;
